import React, { useContext } from 'react'
import { Context } from '../../../../context'
import useValue from '../../../../hooks/useValue'
import MyModal from '../../../../Components/UI/MyModal/MyModal'
import MyTitle from '../../../../Components/MyTitle/MyTitle'

const Statistics = () => {
  const [inputValue, setInputValue, inputOnChange] = useValue('')
  const { darkMode, modals, openModals } = useContext(Context)

  const styleObj = {
    modalStyles: {
      modal: {
        backgroundColor: '#0007',
        transition: 'all .0.2s ease 0s',
      },
      modalContainer: {
        maxWidth: '700px',
        padding: '20px',
        borderRadius: '10px',
        transition: 'all 0.5s ease 0.1s',
      },
    },
    selectStyles: {
      width: '200px',
      padding: '8px 15px',
      border: '1px solid rgba(40, 40, 70, 0.3)',
      borderRadius: '8px',
      fontWeight: '400',
      fontSize: '14px',
      lineHeight: '19px',
      letterSpacing: '0.02em',
      option: {
        fontSize: '16px',
        color: '#666',
      },
    },
  }

  if (window.innerWidth <= 767) {
    modalStyles.modalContainer.maxWidth = '330px'
    modalStyles.modalContainer.padding = '15px'
    modalStyles.modalContainer.borderRadius = '8px'
  }

  if (window.innerWidth <= 414) {
    modalStyles.modalContainer.maxWidth = '290px'
    modalStyles.modalContainer.padding = '10px'
  }

  return (
    <MyModal
      styles={styleObj.modalStyles}
      darkMode={darkMode}
      opened={modals.statisticsModal}
      closeModal={() => openModals({ ...modals, statisticsModal: false })}>
      <MyTitle fontWeight='700' fontSize='20px' lineHeight='27px' letterSpacing='0.02em'>
        Statistics
      </MyTitle>
      <Select
        styles={styleObj.selectStyles}
        options={tabItems.tasks}
        value={category}
        onChange={categorySelectOnChange}
      />
    </MyModal>
  )
}

export default Statistics
