const ActiveTasks = ({ MyTitle, TransitionGroup, CSSTransition, Task, ...props }) => {
  return (
    <div className='category__activeTasks'>
      <MyTitle {...props.categoryTitleStyles}>Active tasks</MyTitle>

      <TransitionGroup
        className={
          props.darkMode ? 'activeTasksContainer darkMode' : 'activeTasksContainer'
        }>
        {props.category.data
          .filter(task => !task.completed)
          .map(uncompletedTask => {
            const nodeRef = createRef(null)
            return (
              <CSSTransition
                key={uncompletedTask.time}
                nodeRef={nodeRef}
                timeout={500}
                classNames='task'>
                <Task
                  time={uncompletedTask.time}
                  currentTask={uncompletedTask}
                  tabTitle={props.category.title}
                  deleteTask={deleteTask}
                  completed={uncompletedTask.completed}
                  checkTask={checkTask}
                  editTask={editTask}
                  timeLine={timeLine}
                  ref={nodeRef}>
                  {uncompletedTask.task}
                </Task>
              </CSSTransition>
            )
          })}
      </TransitionGroup>
    </div>
  )
}

export default ActiveTasks
