import React, { useContext } from 'react'
import './authorization.scss'
import MyModal from '../../Components/UI/MyModal/MyModal'
import Input from '../../Components/UI/Input/Input'
import { Context } from '../../context'

const Authorization = () => {
  const { darkMode, authModal, setAuthModal } = useContext(Context)

  const styleObj = {
    modalStyles: {
      modal: {
        backgroundColor: '#0007',
        transition: 'all .0.2s ease 0s',
      },
      modalContainer: {
        maxWidth: '700px',
        padding: '20px',
        borderRadius: '10px',
        transition: 'all 0.5s ease 0.1s',
      },
    },
  }

  return (
    <MyModal
      styles={styleObj.modalStyles}
      darkMode={darkMode}
      opened={authModal}
      closeModal={() => setAuthModal(false)}>
      <div className='auth__tabs-name'>
        <button type='button' className='auth__btn'>
          Login
        </button>
        <button type='button' className='auth__btn'>
          Registration
        </button>
      </div>
      <div className='auth__tabs-form'>
        <div className='auth__tab'>
          <form className='auth__form'>
            <Input />
            <Input />
          </form>
        </div>
        <div className='auth__tab'></div>
      </div>
    </MyModal>
  )
}

export default Authorization
