import React, { useState } from 'react'
import cl from './modeBtn.module.scss'
import moon from '../../assets/icons/mode/moon.png'
import sun from '../../assets/icons/mode/sun.png'

const ModeBtn = () => {
  const [mode, setMode] = useState('light')

  const classesLight = [cl.modeBtn__light, cl.active],
    classesDark = [cl.modeBtn__dark]

  const checkMode = () => {
    if (mode === 'light') {
      classesLight.push(cl.active)
    }

    if (mode === 'dark') {
      classesDark.push(cl.active)
    }

    return setMode('dark')
  }

  return (
    <button type='button' className={cl.modeBtn} onToggle={() => checkMode()}>
      <img src={moon} alt='moon' className={classesLight.join(' ')} />
      <img src={sun} alt='sun' className={classesDark.join(' ')} />
    </button>
  )
}

export default ModeBtn
