const getDataFromLS = key => JSON.parse(localStorage.getItem(key))

const addDataInLS = (key, value) => {
  const getCircularReplacer = () => {
    const seen = new WeakSet()
    return (key, value) => {
      if (typeof value === 'object' && value !== null) {
        if (seen.has(value)) {
          return
        }
        seen.add(value)
      }
      return value
    }
  }

  return localStorage.setItem(key, JSON.stringify(value, getCircularReplacer(value)))
}

export { getDataFromLS, addDataInLS }
