import React from 'react'
import Select from '../../../UI/Select/Select'
import './categoriesConfig.scss'

const CategoriesConfig = ({ modal, tab, filterType, filterTasks, removeCategory }) => {
  const filteringTasks = () => {
    return filterTasks()
  }

  console.log(category)

  const clConfigBtn = ['category__configModal']

  if (modal) clConfigBtn.push('open')

  const selectStyles = {
    marginLeft: '10px',
    padding: '3px',
    borderRadius: '15px',
    fontSize: '14px',
    cursor: 'pointer',
  }

  return (
    <div className={clConfigBtn.join(' ')}>
      <div className='category__filter'>
        <span>filter by:</span>
        <Select
          styles={selectStyles}
          options={[{ title: 'newest first' }, { title: 'oldest first' }]}
          value={filterType}
          onChange={filteringTasks}
        />
      </div>
      <button
        type='button'
        className='category__removeBtn'
        title='remove this category'
        onClick={removeCategory}>
        Remove
      </button>
    </div>
  )
}

export default CategoriesConfig
