import React from 'react'
import cl from './myModal.module.scss'

const MyModal = ({ children, ...props }) => {
  const classes = { modal: [cl.taskModal], modal__container: [cl.taskModal__container] }

  console.log(props)
  if (props.taskModal) classes.modal.push(cl.open)
  if (props.darkMode) classes.modal__container.push(cl.darkMode)

  return (
    <div className={classes.modal.join(' ')}>
      <div className={classes.modal__container.join(' ')} onClick={props.onClick}>
        {children}
      </div>
    </div>
  )
}

export default MyModal
