import { initializeApp } from 'firebase/app'
import {
  getAuth,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  onAuthStateChanged,
  signOut,
} from 'firebase/auth'
import { getDatabase, ref, set, get, child } from 'firebase/database'

const firebaseConfig = {
  apiKey: process.env.REACT_APP_FIREBASE_API_KEY,
  authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN,
  databaseURL: process.env.REACT_APP_FIREBASE_DATABASE_URL,
  projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID,
  storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET,
  messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID,
  appId: process.env.REACT_APP_FIREBASE_APP_ID,
}

const app = initializeApp(firebaseConfig)
const auth = getAuth(app)
const db = getDatabase(app)

let userId = ''
// database
const database = {
  writeUserData: async (userInfo, data) => {
    return await set(ref(db, `${userId}/${userId}`), {
      username: userInfo.nick,
      email: userInfo.email,
      profile_picture: userInfo.photo,
      userData: data,
    })
  },
  readUserData: async (setUserInfo, setTabItem) => {
    try {
      const snapshot = await get(child(ref(db), `${userId}/${userId}`))
      if (snapshot.exists()) {
        // console.log(snapshot.exists())
        const userData = snapshot.val()
        setUserInfo({
          photo: userData.profile_picture,
          nick: userData.username,
          email: userData.email,
        })

        // console.log(userData.userData)
        //setTabItem([...userData.userData])
      } else {
        console.log(snapshot.exists())
      }
    } catch (Error) {
      console.error(Error)
    }
  },
}

// Authentication methods
const authentication = {
  // Login
  loginEmailPassword: async ({ ...params }) => {
    if (params.login.Password) {
      try {
        const userCredential = await signInWithEmailAndPassword(
          auth,
          params.login.Email,
          params.login.Password,
        )

        userId = userCredential.user.uid

        params.setLogin({ Email: '', Password: '' })

        setTimeout(() => {
          params.setAuthModal(false)
        }, 1000)

        params.createAuthInfoModal({
          modal: true,
          type: 'Success',
          text: 'Authorization successfully',
        })
      } catch (Error) {
        params.createAuthInfoModal({ modal: true, type: 'Error', text: Error.code })
      }
    }
  },
  // Registration
  registrationEmailPassword: async ({ ...params }) => {
    if (params.registration.Password) {
      try {
        await createUserWithEmailAndPassword(
          auth,
          params.registration.Email,
          params.registration.Password,
        )

        params.setRegistration({ Email: '', Password: '' })

        setTimeout(() => {
          params.setAuthModal(false)
          params.navigate('Profile.jsx')
        }, 1000)

        params.createAuthInfoModal({
          modal: true,
          type: 'Success',
          text: 'Registration successfully',
        })
      } catch (Error) {
        params.createAuthInfoModal({ modal: true, type: 'Error', text: Error.code })
      }
    }
  },
  // State
  monitorAuthState: async (setAuthorization, defaultPhoto, setUserInfo) => {
    await onAuthStateChanged(auth, user => {
      if (user) {
        setAuthorization(true)
      } else {
        setAuthorization(false)
        setUserInfo({ photo: defaultPhoto, nick: 'username', email: '' })
      }
    })
  },
  // Logout
  logOut: async () => await signOut(auth),
}

export { authentication, database }
