import React, { useState } from 'react'
import cl from './addBtn.module.scss'
import Input from '../Input/Input'

const AddBtn = () => {
  const [invisible, setInvisible] = useState(false)

  const btnClasses = [cl.addBtn__btn]

  if (invisible) {
    btnClasses.push(cl.invisible)
  }

  return (
    <div className={cl.addBtn__container}>
      <Input padding='6px' fontSize='14px' />
      <button type='button' className={btnClasses.join(' ')} onClick={() => setInvisible(true)}>
        add category
      </button>
    </div>
  )
}

export default AddBtn
