import { useNavigate, useLocation } from 'react-router-dom'
import './logo.scss'

const Logo = () => {
  const location = useLocation()
  const navigate = useNavigate()

  const handleClick = () => {
    navigate.pathname = '/' ? window.location.reload() : navigate('/')
  }

  return (
    <a className='asideBar__logo' onClick={handleClick}>
      Tasks Book
    </a>
  )
}

export default Logo
