const MainBtn = props => {
  const { className, type, onClick, disabled, children } = props

  const defaultBtnStyles = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    opacity: disabled && 0.5,
    cursor: disabled && 'not-allowed',
  }

  return (
    <button {...props} style={defaultBtnStyles}>
      {children}
    </button>
  )
}

export default MainBtn
