import React, { useContext, useRef } from 'react'
import { Context } from '../../../../context'
import './listItem.scss'

const ListItem = ({ children, onClick }) => {
  const { darkMode, tab } = useContext(Context)
  const ref = useRef(null)

  const handleClick = () => {
    if (ref.current !== null && tab === ref.current.outerText) {
      if (ref.current.classList.contains('selected')) {
        ref.current.classList.remove('selected')
      }

      ref.current.classList.add('selected')

      console.log(ref.current)
    }
  }

  return (
    <button
      ref={ref}
      type='button'
      className={darkMode ? 'listItem darkMode' : 'listItem'}
      onClick={onClick}>
      {children}
    </button>
  )
}

export default ListItem
