const MainBtn = props => {
  const { className, type, onClick, disabled, children } = props

  const defaultBtnStyles = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    transition: 'all 0.2s ease 0s',
    opacity: disabled && 0.5,
    cursor: 'not-allowed',
  }

  return (
    <button
      className={className}
      type={type}
      style={defaultBtnStyles}
      onClick={onClick}
      disabled={disabled}>
      {children}
    </button>
  )
}

export default MainBtn
