import React from 'react'
import './listItem.scss'

const ListItem = ({ children, darkMode, onClick }) => {
  const fz = { fontSize: 'calc(18px + 6 * (100vw/1920))' }

  if (typeof children === 'string' && children.length >= 16) {
    fz.fontSize = '14px'
  }

  if (window.innerWidth <= 1600) {
    fz.fontSize = '16px'

    if (typeof children === 'string' && children.length >= 15) {
      fz.fontSize = '14px'
    }
  }

  if (window.innerWidth <= 1400) {
    fz.fontSize = '14px'

    if (typeof children === 'string' && children.length >= 15) {
      fz.fontSize = '12px'
    }
  }

  return (
    <button
      style={fz}
      type='button'
      className={darkMode ? 'listItem darkMode' : 'listItem'}
      onClick={onClick}>
      {children}
    </button>
  )
}

export default ListItem
