import { initializeApp } from 'firebase/app'
import {
  getAuth,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  onAuthStateChanged,
} from 'firebase/auth'

const firebaseConfig = {
  apiKey: 'AIzaSyB_MjACGz_BR2HoSjrGos_DWC0eyDtG890',
  authDomain: 'task-manager-19b7a.firebaseapp.com',
  databaseURL: 'https://task-manager-19b7a-default-rtdb.firebaseio.com',
  projectId: 'task-manager-19b7a',
  storageBucket: 'task-manager-19b7a.appspot.com',
  messagingSenderId: '697303553114',
  appId: '1:697303553114:web:3c65db84a8cd1186557463',
}

const app = initializeApp(firebaseConfig)
const auth = getAuth(app)

// Authentication methods
const authentication = {
  // Login
  loginEmailPassword: async ({ props }) => {
    if (props.login.Password) {
      try {
        const userCredential = await signInWithEmailAndPassword(
          auth,
          props.login.Email,
          props.login.Password,
        )

        props.setLogin({ Email: '', Password: '' })

        setTimeout(() => {
          props.setAuthModal(false)
          props.setAuthorization(true)
        }, 1000)

        props.createAuthInfoModal({
          modal: true,
          type: 'Success',
          text: 'Authorization successfully',
        })
      } catch (Error) {
        props.createAuthInfoModal({ modal: true, type: 'Error', text: Error.code })
      }
    }
  },
  // Registration
  registrationEmailPassword: async props => {
    if (props.registration.Password) {
      try {
        const userCredential = await createUserWithEmailAndPassword(
          auth,
          props.registration.Email,
          props.registration.Password,
        )

        props.setRegistration({ Email: '', Password: '' })

        setTimeout(() => {
          props.setAuthModal(false)
          props.setAuthorization(true)
        }, 1000)

        props.createAuthInfoModal({
          modal: true,
          type: 'Success',
          text: 'Registration successfully',
        })
      } catch (Error) {
        props.createAuthInfoModal({ modal: true, type: 'Error', text: Error.code })
      }
    }
  },
  monitorAuthState: async () => {
    onAuthStateChanged(auth, user => {
      if (user) {
        console.log(user)
      } else {
        console.log(`You'are not logged in...`)
      }
    })
  },
}

export { authentication }
