const TasksContainer = ({ MyTitle, TransitionGroup, CSSTransition, Task, ...props }) => {
  const classNames = {
    block: props.type === 'Active' ? 'activeTasks__block' : 'completedTasks__block',
    container:
      props.type === 'Active' ? 'activeTasks__container' : 'completedTasks__container',
  }

  return (
    <div className={classNames.block}>
      <MyTitle {...props.categoryTitleStyles}>{props.type} tasks</MyTitle>

      <TransitionGroup className={classNames.container}>
        {props.tasksData
          .filter(task => (props.type === 'Active' ? !task.completed : task.completed))
          .map(task => {
            const nodeRef = props.createRef(null)
            return (
              <CSSTransition
                key={task.time}
                nodeRef={nodeRef}
                timeout={500}
                classNames='task'>
                <Task
                  time={task.time}
                  currentTask={task}
                  tabTitle={props.tabTitle}
                  // completed={task.completed}
                  deleteTask={props.tasksMethods.deleteTask}
                  checkTask={props.tasksMethods.checkTask}
                  editTask={props.tasksMethods.editTask}
                  timeLine={props.timeLine}
                  darkMode={props.darkMode}
                  ref={nodeRef}>
                  {task.task}
                </Task>
              </CSSTransition>
            )
          })}
      </TransitionGroup>
    </div>
  )
}

export default TasksContainer
