import React, { useRef } from 'react'
import { CSSTransition, TransitionGroup } from 'react-transition-group'
import CategoriesTab from './CategoriesTab/CategoriesTab'
import './tasks.scss'

const Tasks = props => {
  const nodeRef = useRef(null)

  if (props.tabItems.length) {
    return (
      <div
        className={
          props.darkMode
            ? 'mainContent__tabsContainer darkMode'
            : 'mainContent__tabsContainer'
        }>
        <TransitionGroup>
          {props.tabItems.map((category, idx) => (
            <CSSTransition
              nodeRef={nodeRef}
              in={props.showTab}
              timeout={200}
              classNames='myTab'
              key={category.title}>
              <CategoriesTab
                darkMode={props.darkMode}
                category={category}
                idx={idx}
                tab={props.tab}
                setTab={props.setTab}
                setCategory={props.setCategory}
                tabItems={props.tabItems}
                setTabItem={props.setTabItem}
                deleteTask={props.deleteTask}
                checkTask={props.checkTask}
                editTask={props.editTask}
                ref={nodeRef}
              />
            </CSSTransition>
          ))}
        </TransitionGroup>
      </div>
    )
  }

  return <div className='noCategory'>create a category and add tasks</div>
}

export default Tasks
