import React from 'react'
import cl from './modeBtn.module.scss'
import moon from '../../assets/icons/mode/moon.png'
import sun from '../../assets/icons/mode/sun.png'

const ModeBtn = ({ darkMode, setDarkMode }) => {
  return (
    <button type='button' className={cl.modeBtn} onClick={() => setDarkMode(!darkMode)}>
      <img src={darkMode ? sun : moon} alt='mode' />
    </button>
  )
}

export default ModeBtn
