import React, { useContext } from 'react'
import './authorization.scss'
import MyModal from '../../Components/UI/MyModal/MyModal'
import MyTitle from '../../Components/MyTitle/MyTitle'
import Input from '../../Components/UI/Input/Input'
import { Context } from '../../context'
import MainBtn from '../../Components/UI/MainBtn/MainBtn'

const Authorization = () => {
  const { darkMode, authModal, setAuthModal } = useContext(Context)

  const styleObj = {
    modalStyles: {
      modal: {
        backgroundColor: '#0007',
        transition: 'all .0.2s ease 0s',
      },
      modalContainer: {
        maxWidth: '700px',
        padding: '20px',
        borderRadius: '10px',
        transition: 'all 0.5s ease 0.1s',
      },
    },
    inputStyles: {
      width: '310px',
      padding: '10px 15px',
      fontWeight: '400',
      fontSize: '16px',
      lineHeight: '19px',
      letterSpacing: '0.02em',
      color: '#0007',
    },
    btnStyles: {
      padding: '10px 25px',
      fontSize: '16px',
      lineHeight: '22px',
      letterSpacing: '0.01em',
      color: '#FAFAFA',
    },
  }

  return (
    <MyModal
      styles={styleObj.modalStyles}
      darkMode={darkMode}
      opened={authModal}
      closeModal={() => setAuthModal(false)}>
      <MyTitle
        fontSize='18px'
        lineHeight='25px'
        letterSpacing='0.02em'
        textAlign='center'>
        Log in to your account
      </MyTitle>
      <form className='auth__form'>
        <Input type='text' style={styleObj.inputStyles} placeholder='E-mail' />
        <Input type='password' style={styleObj.inputStyles} placeholder='Password' />
        <MainBtn>Entrance</MainBtn>
      </form>
    </MyModal>
  )
}

export default Authorization
