import React from 'react'
import Input from '../../../../../Components/UI/Input/Input'

const InputContainer = React.forwardRef((props, ref) => {
  const { addNewCategory, inputValue, setInputValue, inputContainer, showInputContainer } = props

  const enterKeyUp = e => (e.code === 'Enter' ? addNewCategory() : false)

  return (
    <div className={inputContainer ? 'addItem__inputContainer visible' : 'addItem__inputContainer'}>
      <Input
        className='addItem__input'
        placeholder='add category'
        value={inputValue}
        onChange={setInputValue}
        onKeyUp={enterKeyUp}
        maxLength={18}
        ref={ref}
      />
      <div className='btn__container'>
        <button type='button' className='addItem__addItem' onClick={addNewCategory}>
          add
        </button>
        <button type='button' className='addItem__close' onClick={showInputContainer}>
          cancel
        </button>
      </div>
    </div>
  )
})

export default InputContainer
