import React, { useState } from 'react'
import cl from './addItemList.module.scss'
import Input from '../Input/Input'

const AddItemList = () => {
  const [state, setState] = useState({ invisible: false, visible: false })

  const addBtn = [cl.addItemList__btn],
    inputContainer = [cl.addItemList__input]

  if (state.invisible || state.visible) {
    addBtn.push(cl.invisible)
    inputContainer.push(cl.visible)
  }

  return (
    <div>
      <div className={inputContainer.join(' ')}>
        <Input padding='6px' fontSize='14px' />
        <button type='button' className={cl.addItemList__close}>
          X
        </button>
      </div>
      <button
        type='button'
        className={addBtn.join(' ')}
        onClick={() => setState({ invisible: true, visible: true })}>
        add category
      </button>
    </div>
  )
}

export default AddItemList
