import useValue from '../hooks/useValue'
import useLocaleStorage from '../hooks/useLocaleStorage'
import { useEffect } from 'react'

const defaultItems = [
  { title: 'Home', data: [], sortType: 'newest first' },
  { title: 'Work', data: [], sortType: 'newest first' },
  { title: 'Sport', data: [], sortType: 'newest first' },
]

const withApp = Component => {
  return () => {
    const [darkMode, setDarkMode] = useLocaleStorage('darkMode', false),
      [taskModal, setTaskModal] = useValue(false),
      [tabsStorage, setDataInStorage] = useLocaleStorage('data', defaultItems),
      [tabItems, setTabItem] = useValue(tabsStorage.length ? tabsStorage : defaultItems),
      [tab, setTab] = useValue(tabItems[0].title),
      [category, setCategory, categorySelectOnChange] = useValue(defaultItems[0].title)

    const sortingTasks = (a, b) => {
      const firstTask = a.date.time.split(':').join('') + a.date.dmy.split('.').join('')
      const secondTask = b.date.time.split(':').join('') + b.date.dmy.split('.').join('')

      return tab === 'newest first' ? secondTask - firstTask : firstTask - secondTask
    }

    const setSortType = e => {
      const arr = props.tabItems

      for (let i = 0; i < arr.length; i++) {
        if (arr[i].title === props.tab) {
          arr[i].sortType = e.target.value
        }
      }

      return props.setTabItem(props.tabItems)
    }

    useEffect(() => {
      setDataInStorage(tabItems.sort(sortingTasks))
    }, [tabItems])

    const taskMethods = {
      createTask: (inputValue, setInputValue) => {
        setInputValue('')
        setTaskModal(false)
        setTab(category)

        tabItems.forEach(tab => {
          if (tab.title === category) {
            tab.data = [
              {
                task: inputValue,
                date: {
                  time: new Date().toLocaleTimeString(),
                  dmy: new Date().toLocaleDateString(),
                },
                completed: false,
              },
              ...tab.data,
            ]
          }
        })

        return setTabItem([...tabItems])
      },

      deleteTask: (title, currentTask) => {
        tabItems.forEach(tab => {
          if (title === tab.title) {
            tab.data = tab.data.filter(task => task.date.time !== currentTask.date.time)
          }
        })

        return setTabItem([...tabItems])
      },

      checkTask: (title, currentTask, complete) => {
        tabItems.forEach(tab => {
          if (title === tab.title) {
            tab.data.forEach(task => {
              if (currentTask.date.time === task.date.time) task.completed = !complete
            })
          }
        })

        return setTabItem(tabItems => [...tabItems])
      },

      editTask: (event, title, currentTask, newValue) => {
        event.target.style.borderBottom = `1px solid ${
          event.target.value ? 'transparent' : 'red'
        }`
        event.target.readOnly = event.code === 'Enter' && event.target.value

        tabItems.forEach(tab => {
          if (title === tab.title) {
            tab.data.forEach(task => {
              if (currentTask.task === task.task) task.task = newValue
            })
          }
        })

        return setTabItem(tabItems => [...tabItems])
      },
    }

    return (
      <Component
        darkMode={darkMode}
        setDarkMode={setDarkMode}
        taskModal={taskModal}
        setTaskModal={setTaskModal}
        tab={tab}
        setTab={setTab}
        tabItems={tabItems}
        setTabItem={setTabItem}
        category={category}
        setCategory={setCategory}
        categorySelectOnChange={categorySelectOnChange}
        {...taskMethods}
      />
    )
  }
}

export { withApp }
