import React, { useState } from 'react'
import './App.scss'
import AsideBar from '../AsideBar/AsideBar'
import MainContent from '../MainContent/MainContent'
import TaskModal from '../TaskModal/TaskModal'

const defaultListItems = [
  { title: 'Home', data: [] },
  { title: 'Work', data: [] },
  { title: 'Sport', data: [] },
]

const App = () => {
  const [darkMode, setDarkMode] = useState(false)
  const [listItems, setListItem] = useState(defaultListItems)
  const [tab, setTab] = useState(defaultListItems[0].title)
  const [taskModal, setTaskModal] = useState(false)

  const createTask = (task, setTask) => {
    setTask({ input: '', select: '' })
    setTaskModal(false)

    listItems.forEach(tab => {
      if (tab.title === task.select) {
        tab.data.push({ task: task.input, date: new Date(), completed: false })
      }
    })

    return setListItem(listItems)
  }
  console.log(listItems)

  const deleteTask = (event, title, element) => {
    event.stopPropagation()

    console.log(title, element)

    let filteredArr = []

    for (const tab of listItems) {
      if (title === tab.title) {
        filteredArr = tab.data.filter(task => task.task !== element.task)
      }
    }

    return setListItem([...{ ...listItems, data: filteredArr }])
  }

  return (
    <div className={darkMode ? 'App darkMode' : 'App'}>
      <div className='app__container'>
        <AsideBar
          darkMode={darkMode}
          listItems={listItems}
          setListItem={setListItem}
          tab={tab}
          setTab={setTab}
        />
        <MainContent
          darkMode={darkMode}
          setDarkMode={setDarkMode}
          setTaskModal={setTaskModal}
          listItems={listItems}
          setListItem={setListItem}
          deleteTask={deleteTask}
          tab={tab}
          setTab={setTab}
        />
        <TaskModal
          darkMode={darkMode}
          taskModal={taskModal}
          setTaskModal={setTaskModal}
          listItems={listItems}
          createTask={createTask}
        />
      </div>
    </div>
  )
}

export default App
