import React, { useState } from 'react'
import cl from './addBtn.module.scss'
import Input from '../Input/Input'

const AddBtn = () => {
  const [invisible, setInvisible] = useState('false')

  return (
    <div className={cl.addBtn__container}>
      <Input padding='6px' fontSize='14px' />
      <button
        type='button'
        className={invisible ? [cl.addBtn__btn].push('invisible').join(' ') : cl.addBtn__btn}
        onClick={() => setInvisible(true)}>
        add category
      </button>
    </div>
  )
}

export default AddBtn
