import React, { useState } from 'react'
import './App.scss'
import AsideBar from '../AsideBar/AsideBar'
import MainContent from '../MainContent/MainContent'
import TaskModal from '../TaskModal/TaskModal'

const defaultListItems = [
  {
    title: 'Home',
    data: [
      { task: 'Learn Git', time: new Date(), completed: true },
      { task: 'Learn Html', time: new Date(), completed: true },
      { task: 'Learn Css', time: new Date(), completed: true },
      { task: 'Learn Next.js', time: new Date(), completed: false },
      { task: 'Learn Js', time: new Date(), completed: true },
      { task: 'Learn React.js', time: new Date(), completed: false },
    ],
  },
  {
    title: 'Work',
    data: [
      { task: 'Learn Html', time: new Date(), completed: true },
      { task: 'Learn Js', time: new Date(), completed: true },
      { task: 'Learn React', time: new Date(), completed: false },
    ],
  },
  {
    title: 'Sport',
    data: [
      { task: 'Learn Html,Css', time: new Date(), completed: true },
      { task: 'Learn React', time: new Date(), completed: false },
      { task: 'Learn Js', time: new Date(), completed: true },
    ],
  },
]

const App = () => {
  const [darkMode, setDarkMode] = useState(false)
  const [listItems, setListItem] = useState(defaultListItems)
  const [tab, setTab] = useState(defaultListItems[0].title)
  const [taskModal, setTaskModal] = useState(false)

  const createTask = task => {
    for (const tab of listItems) {
      if (tab.title === task.input) {
        console.log(tab)
      }
    }

    console.log(task)
  }

  return (
    <div className={darkMode ? 'App darkMode' : 'App'}>
      <div className='app__container'>
        <AsideBar
          darkMode={darkMode}
          listItems={listItems}
          setListItem={setListItem}
          tab={tab}
          setTab={setTab}
        />
        <MainContent
          darkMode={darkMode}
          setDarkMode={setDarkMode}
          setTaskModal={setTaskModal}
          listItems={listItems}
          setListItem={setListItem}
          tab={tab}
          setTab={setTab}
        />
        <TaskModal
          darkMode={darkMode}
          taskModal={taskModal}
          setTaskModal={setTaskModal}
          listItems={listItems}
          createTask={createTask}
        />
      </div>
    </div>
  )
}

export default App
