import { useContext } from 'react'
import { Context } from '../../../../context'
import MyModal from '../../../../Components/UI/MyModal/MyModal'
import MainBtn from '../../../../Components/UI/MainBtn/MainBtn'

const DeleteModal = ({ removeCategory }) => {
  const { darkMode, modals, openModals } = useContext(Context)

  const closeTaskModal = () => openModals({ ...modals, deleteCategoryModal: false })

  const btnStyles = {
    padding: '10px 25px',
    backgroundColor: 'transparent',
    border: '1px solid  #29a19c',
    borderRadius: '8px',
    fontSize: '16px',
  }

  return (
    <MyModal
      darkMode={darkMode}
      opened={modals.deleteCategoryModal}
      closeModal={closeTaskModal}>
      delete?
      <div className='removeCategory__btnContainer'>
        <MainBtn type='button' styles={btnStyles}>
          yes
        </MainBtn>
        <MainBtn type='button' styles={btnStyles}>
          no
        </MainBtn>
      </div>
    </MyModal>
  )
}

export default DeleteModal
