import { useContext } from 'react'
import {
  ResponsiveContainer,
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
} from 'recharts'
import MyTitle from '../../../../Components/MyTitle/MyTitle'
import { Context } from '../../../../context'
import './chart.scss'

const data = [
  { day: 'Mon', completed: 2 },
  { day: 'Tue', completed: 5 },
  { day: 'Wed', completed: 7 },
  { day: 'Thu', completed: 3 },
  { day: 'Fri', completed: 8 },
  { day: 'Sat', completed: 4 },
  { day: 'Sun', completed: 9 },
]

const Chart = () => {
  const { darkMode } = useContext(Context)

  const chartNotify = {
    responsiveContainer: { width: '100%', height: 200 },
    lineChart: {
      data: data,
      margin: { top: 0, right: 0, left: -28, bottom: 5 },
    },
    tooltip: {
      cursor: { stroke: 'red', strokeWidth: 2 },
    },
    line: {
      type: 'natural',
      dataKey: 'completed',
      strokeWidth: 3,
      activeDot: {
        strokeWidth: 2,
        r: 10,
      },
      isAnimationActive: true,
      stroke: '#29A19C',
    },
  }

  if (window.matchMedia('(max-width: 1600px)').matches) {
    chartNotify.height = 170
  }

  return (
    <div className={darkMode ? 'chart darkMode' : 'chart'}>
      <MyTitle fontSize='18px' lineHeight='25px' letterSpacing='0.02em' marginBottom='15px'>
        Progress chart
      </MyTitle>
      <ResponsiveContainer {...chartNotify.responsiveContainer}>
        <LineChart {...chartNotify.lineChart}>
          <CartesianGrid />
          <XAxis dataKey='day' />
          <YAxis />
          <Tooltip {...chartNotify.tooltip} />
          <Line {...chartNotify.line} />
        </LineChart>
      </ResponsiveContainer>
    </div>
  )
}

export default Chart
