import React, { useState } from 'react'
import cl from './modeBtn.module.scss'
import moon from '../../assets/icons/mode/moon.png'
import sun from '../../assets/icons/mode/sun.png'

const ModeBtn = () => {
  const [mode, setMode] = useState('light')

  const classesLight = [cl.modeBtn__light],
    classesDark = [cl.modeBtn__dark]

  mode === 'light' ? [...classesLight, cl.active] : classesDark.push(cl.active)

  return (
    <button
      type='button'
      className={cl.modeBtn}
      onClick={() => setMode(mode === 'light' ? 'dark' : 'light')}>
      <img src={moon} alt='moon' className={classesLight.join(' ')} />
      <img src={sun} alt='sun' className={classesDark.join(' ')} />
    </button>
  )
}

export default ModeBtn
