import React, { useState } from 'react'
import cl from './modeBtn.module.scss'

const ModeBtn = () => {
  const [mode, setMode] = useState('dark')

  const classesLight = [cl.modeBtn__light],
    classesDark = [cl.modeBtn__dark]

  if (mode === 'light') {
    classesLight.push(cl.active)
  }
  if (mode === 'dark') {
    classesDark.push(cl.active)
  }

  return (
    <button type='button' className={cl.modeBtn} onClick={() => setMode('light')}>
      <img src='../../assets/icons/mode/moon.png' alt='moon' className={classesLight.join(' ')} />
      <img src='../../assets/icons/mode/sun.png' alt='sun' className={classesDark.join(' ')} />
    </button>
  )
}

export default ModeBtn
