import React, { useEffect } from 'react'
import { Routes, Route } from 'react-router-dom'
import { withApp } from '../hoc/withApp'
import Layout from '../Components/Layout/Layout'
import Main from '../Pages/main-page/Main'
import Profile from '../Pages/profile-page/Profile'
import './App.scss'

const App = ({ darkMode }) => {
  useEffect(() => {
    const clickApp = e => {
      console.log(true)

      if (e.target.className === 'asideBar') {
        return console.log(e.target)
      }
    }

    window.addEventListener('click', clickApp, { passive: true })

    return window.removeEventListener('click', clickApp)
  })

  return (
    <Routes>
      <Route path='/' element={<Layout darkMode={darkMode} />}>
        <Route index element={<Main />} />
        <Route path='Profile.jsx' element={<Profile darkMode={darkMode} />} />
      </Route>
    </Routes>
  )
}

export default withApp(App)
