import { useContext } from 'react'
import {
  ResponsiveContainer,
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
} from 'recharts'
import MyTitle from '../../../../Components/MyTitle/MyTitle'
import { Context } from '../../../../context'
import './chart.scss'

const data = [
  { day: 'Mon', completed: 2 },
  { day: 'Tue', completed: 7 },
  { day: 'Wed', completed: 13 },
  { day: 'Thu', completed: 4 },
  { day: 'Fri', completed: 5 },
  { day: 'Sat', completed: 15 },
  { day: 'Sun', completed: 9 },
]

const CustomTooltip = ({ active, payload }) => {
  if (active && payload && payload.length) {
    return (
      <div className='chart__custom-tooltip'>
        <p className='label'>{payload[0].payload.day}</p>
        <p className='label'>
          {payload[0].dataKey} : {payload[0].value}
        </p>
      </div>
    )
  }

  return null
}
const Chart = () => {
  const { darkMode } = useContext(Context)

  const chartNotify = {
    ResponsiveContainer: { width: '100%', height: 200 },
    Tooltip: {
      content: <CustomTooltip />,
      cursor: { stroke: darkMode ? 'lightgreen' : 'green', strokeWidth: 1 },
    },
    Line: {
      type: 'natural',
      dataKey: 'completed',
      strokeWidth: 3,
      activeDot: {
        strokeWidth: 0,
        r: 10,
      },
      isAnimationActive: true,
      stroke: '#29A19C',
    },
  }

  if (window.matchMedia('(max-width: 1600px)').matches) {
    chartNotify.ResponsiveContainer.height = 170
  }

  return (
    <div className={darkMode ? 'chart darkMode' : 'chart'}>
      <MyTitle fontSize='18px' lineHeight='25px' letterSpacing='0.02em' marginBottom='15px'>
        Progress chart
      </MyTitle>
      <ResponsiveContainer {...chartNotify.ResponsiveContainer}>
        <LineChart data={data}>
          <CartesianGrid />
          <XAxis dataKey='day' />
          <YAxis width={34} />
          <Tooltip {...chartNotify.Tooltip} />
          <Line {...chartNotify.Line} />
        </LineChart>
      </ResponsiveContainer>
    </div>
  )
}

export default Chart
