import { createRef, useContext } from 'react'
import { CSSTransition, TransitionGroup } from 'react-transition-group'
import { Context } from '../../../../../context'
import { database } from '../../../../../firebase/firebaseConfig'
import useValue from '../../../../../hooks/useValue'
import MyTitle from '../../../../../Components/MyTitle/MyTitle'
import Task from '../Task/Task'
import CategoryHeader from './CategoryHeader'
import TasksContainer from './TasksContainer'
import ActiveTasks from './ActiveTasks'
import CompletedTasks from './CompletedTasks'
import CategoriesConfig from '../CategoriesConfig/CategoriesConfig'
import DeleteModal from '../../../modalWindows/DeleteModal/DeleteModal'
import './categoriesTab.scss'
import { type } from '@testing-library/user-event/dist/type'

const CategoriesTab = props => {
  const [modal, setModal] = useValue(false)

  const {
    darkMode,
    tab,
    setTab,
    setCategory,
    tabItems,
    setTabItems,
    timeLine,
    userInfo,
    calendarDate,
    modals,
    openModals,
    tasksMethods,
  } = useContext(Context)

  const removeCategory = () => {
    const checkingTabs = tabItems.tasks[props.idx - 1] || tabItems.tasks[1]

    setTab(tabItems.tasks.length > 1 && checkingTabs.title)
    setCategory(tabItems.tasks.length > 1 && checkingTabs.title)

    setTabItems({
      ...tabItems,
      tasks: tabItems.tasks.filter(task => task.title !== props.category.title),
    })

    return database.writeUserTasksData(
      userInfo.uid,
      calendarDate.toLocaleDateString().split('.').join(''),
      {
        ...tabItems,
        tasks: tabItems.tasks.filter(task => task.title !== props.category.title),
      },
    )
  }

  const handleDeleteCategory = () => openModals({ ...modals, deleteCategoryModal: true })

  const blockTitleStyles = {
    fontSize: '18px',
    lineHeight: '25px',
    letterSpacing: '0.02em',
  }

  if (window.matchMedia('(max-width: 1400px)').matches) {
    blockTitleStyles.fontSize = '16px'
    blockTitleStyles.lineHeight = '20px'
  }

  const propsObjForTasks = {
    MyTitle,
    TransitionGroup,
    CSSTransition,
    Task,
    blockTitleStyles,
    darkMode,
    tabTitle: props.category.title,
    tasksData: props.category.data,
    tasksMethods,
    timeLine,
    createRef,
  }

  return (
    <div
      className={
        tab === props.category.title
          ? 'category__container active'
          : 'category__container'
      }>
      <CategoryHeader
        categoryTitle={props.category.title}
        modal={modal}
        setModal={setModal}
      />

      {['Active', 'Completed'].map(type => {
        return <TasksContainer key={type} type={type} {...propsObjForTasks} />
      })}

      <CategoriesConfig
        modal={modal}
        timeLine={timeLine}
        category={props.category}
        setSortType={tasksMethods.setSortType}
        handleDeleteCategory={handleDeleteCategory}
        darkMode={darkMode}
      />

      <DeleteModal removeCategory={removeCategory} />
    </div>
  )
}

export default CategoriesTab
