import React, { forwardRef } from 'react'
import { initializeApp } from 'firebase/app'
import { getAuth, signInWithEmailAndPassword } from 'firebase/auth'
import Input from '../../../../Components/UI/Input/Input'
import MainBtn from '../../../../Components/UI/MainBtn/MainBtn'
import useValue from '../../../../hooks/useValue'

const Login = forwardRef(
  ({ inputStyles, btnStyles, setAuthModal, createAuthInfoModal }, ref) => {
    const [login, setLogin] = useValue({ Email: '', Password: '' })
    const [showPass, setShowPass] = useValue(false)

    const firebaseConfig = {
      apiKey: 'AIzaSyB_MjACGz_BR2HoSjrGos_DWC0eyDtG890',
      authDomain: 'task-manager-19b7a.firebaseapp.com',
      databaseURL: 'https://task-manager-19b7a-default-rtdb.firebaseio.com',
      projectId: 'task-manager-19b7a',
      storageBucket: 'task-manager-19b7a.appspot.com',
      messagingSenderId: '697303553114',
      appId: '1:697303553114:web:3c65db84a8cd1186557463',
    }

    const app = initializeApp(firebaseConfig)
    const auth = getAuth(app)

    const loginEmailPassword = async e => {
      e.preventDefault()

      if (login.Email && login.Password) {
        try {
          const userCredential = await signInWithEmailAndPassword(
            auth,
            login.Email,
            login.Password,
          )
          setLogin({ Email: '', Password: '' })
          setAuthModal(false)

          console.log(userCredential.user)
          createAuthInfoModal({ modal: true, text: 'Authorization successfully' })
        } catch (Error) {
          createAuthInfoModal({ modal: true, text: Error })
          console.log(Error)
        }
      }
    }

    return (
      <form className='auth__form'>
        <Input
          style={inputStyles}
          placeholder='E-mail'
          type='email'
          value={login.Email}
          onChange={e => setLogin({ ...login, Email: e.target.value })}
          required
          ref={ref}
        />
        <div className='inputPass__container'>
          <Input
            style={inputStyles}
            placeholder='Password'
            type={showPass ? 'text' : 'password'}
            value={login.Password}
            onChange={e => setLogin({ ...login, Password: e.target.value })}
            minLength={6}
            required
          />
          <button
            type='button'
            className='inputPass__showBtn'
            onClick={() => setShowPass(!showPass)}>
            {showPass ? <span>&#128269;</span> : <span>&#128270;</span>}
          </button>
        </div>

        <MainBtn type='submit' styles={btnStyles} onClick={loginEmailPassword}>
          Entrance
        </MainBtn>
      </form>
    )
  },
)

export default Login
