import React from 'react'
import './task.scss'

const Task = ({ children }) => {
  return (
    <div className='task'>
      <input id={children} type='checkbox' className='taskCheckbox' />
      <label for={children} className='taskName'>
        {children}
      </label>
    </div>
  )
}

export default Task
