import React, { useState } from 'react'
import cl from './input.module.scss'

const Input = ({ ...styles }) => {
  const [inputValue, setInputValue] = useState('')

  return (
    <input
      type='text'
      value={inputValue}
      className={cl.myInput}
      style={styles}
      onChange={e => setInputValue(e.target.value)}
    />
  )
}

export default Input
