const getDataFromLS = key => JSON.parse(localStorage.getItem(key))

const addDataInLS = (key, value) => {
  localStorage.removeItem(key)
  localStorage.setItem(key, JSON.stringify(value))
}

export { getDataFromLS, addDataInLS }
