import Select from '../../../../../Components/UI/Select/Select'
import './categoriesConfig.scss'

const CategoriesConfig = ({
  categoryConfigModal,
  category,
  setSortType,
  timeLine,
  handleDeleteCategory,
}) => {
  const selectStyles = {
    padding: '3px',
    backgroundColor: 'transparent',
    borderRadius: '15px',
    fontSize: '14px',
    color: `${props.darkMode ? '#f9f9f9' : '#282846'}`,
    cursor: 'pointer',
    option: { color: '#282846' },
  }

  return (
    <div className={categoryConfigModal ? 'category__configModal open' : 'category__configModal'}>
      <div className='category__filter'>
        {window.matchMedia('(max-width: 768px)').matches && <span>filter by:</span>}
        <Select
          styles={selectStyles}
          options={[{ title: 'newest first' }, { title: 'oldest first' }]}
          value={category.sortingType}
          onChange={e => setSortType(e.target.value)}
        />
      </div>
      <button
        type='button'
        style={{ display: !timeLine.past ? 'block' : 'none' }}
        className='category__removeBtn'
        title='remove this category'
        onClick={handleDeleteCategory}>
        Remove
      </button>
    </div>
  )
}

export default CategoriesConfig
