import React from 'react'
import Select from '../../../UI/Select/Select'
import './categoriesConfig.scss'
import useValue from '../../../../hooks/useValue'

const CategoriesConfig = ({ modal, tab, removeCategory }) => {
  const [filterType, setFilterType] = useValue('newest first')

  const filteringTasks = e => {
    tabItems.forEach(item => {
      if (item.title === tab) {
        item.filter = filterType
        item.data.sort((a, b) => {
          const firstTask =
            a.date.time.split(':').join('') + a.date.dmy.split('.').join('')
          const secondTask =
            b.date.time.split(':').join('') + b.date.dmy.split('.').join('')

          return filterType === 'newest first'
            ? secondTask - firstTask
            : firstTask - secondTask
        })
      }
    })

    return setTabItem(tabItems)
  }

  const clConfigBtn = ['category__configModal']

  if (modal) clConfigBtn.push('open')

  const selectStyles = {
    marginLeft: '10px',
    padding: '3px',
    borderRadius: '15px',
    fontSize: '14px',
    cursor: 'pointer',
  }

  return (
    <div className={clConfigBtn.join(' ')}>
      <div className='category__filter'>
        <span>filter by:</span>
        <Select
          styles={selectStyles}
          options={[{ title: 'newest first' }, { title: 'oldest first' }]}
          value={filterType}
          onChange={filteringTasks}
        />
      </div>
      <button
        type='button'
        className='category__removeBtn'
        title='remove this category'
        onClick={removeCategory}>
        Remove
      </button>
    </div>
  )
}

export default CategoriesConfig
