import { useEffect } from 'react'
import useValue from '../../../../hooks/useValue'

const StatisticsItem = ({ itemName, quantity }) => {
  const [count, setCount] = useValue(0)
  setTimeout(() => {
    count !== quantity && setCount(count++)
  }, 1000)

  useEffect(() => {}, count)

  return (
    <div className='statistics__item'>
      <span>{itemName}</span>
      <div className='statistics__item-info'>
        <div className='statistics__item-count'>{quantity}</div>
        <span>{itemName ? 'Tasks' : 'Categories'}</span>
      </div>
    </div>
  )
}

export default StatisticsItem
