import React from 'react'
import CategoryTask from './CategoryTask'
import './tasks.scss'

const Tasks = ({ darkMode, listItems, tab }) => {
  const classes = ['mainContent__tasksContainer']

  if (darkMode) classes.push('darkMode')

  return (
    <div className={classes.join(' ')}>
      {listItems.map(category => (
        <CategoryTask key={category.name} category={category} active={category.active} />
      ))}
    </div>
  )
}

export default Tasks
