import useValue from '../hooks/useValue'

const defaultItems = [
  { title: 'Home', data: [] },
  { title: 'Work', data: [] },
  { title: 'Sport', data: [] },
]

const withApp = Component => {
  return () => {
    const [tabItems, setTabItem] = useValue(defaultItems)
    const [tab, setTab] = useValue(defaultItems[0].title)
    const [category, setCategory, selectOnChange] = useValue(defaultItems[0].title)

    // create task
    const createTask = (inputValue, setInputValue) => {
      setInputValue('')
      setTaskModal(false)
      setTab(category)

      tabItems.forEach(tab => {
        if (tab.title === category) {
          tab.data = [...tab.data, { task: inputValue, date: new Date(), completed: false }]
        }
      })

      return setTabItem([...tabItems])
    }

    //  delete task
    const deleteTask = (title, currentTask) => {
      tabItems.forEach(tab => {
        if (title === tab.title) {
          tab.data = tab.data.filter(task => task.task !== currentTask.task)
        }
      })

      return setTabItem([...tabItems])
    }

    // checked task
    const checkTask = (title, currentTask, complete) => {
      tabItems.forEach(tab => {
        if (title === tab.title) {
          tab.data.forEach(task => {
            if (currentTask.task === task.task) task.completed = !complete
          })
        }
      })

      return setTabItem(tabItems => [...tabItems])
    }

    // edit task
    const editTask = (event, title, currentTask, newValue) => {
      event.target.style.borderBottom = `1px solid ${event.target.value ? 'transparent' : 'red'}`
      event.target.readOnly = event.code === 'Enter' && event.target.value

      tabItems.forEach(tab => {
        if (title === tab.title) {
          tab.data.forEach(task => {
            if (currentTask.task === task.task) task.task = newValue
          })
        }
      })

      return setTabItem(tabItems => [...tabItems])
    }

    return <Component />
  }
}

export { withApp }
