import React from 'react'
import cl from './taskModal.module.scss'
import MyTitle from '../MyTitle/MyTitle'

const TaskModal = ({ darkMode, taskModal, setTaskModal }) => {
  const classes = { 1: [cl.taskModal], 2: [cl.taskModal__container] }

  taskModal ? taskModalClasses.push(cl.open) : false

  return (
    <div className={classes[1]} onClick={() => setTaskModal(false)}>
      <div
        className={
          darkMode ? [cl.taskModal__container, cl.darkMode].join(' ') : cl.taskModal__container
        }
        onClick={e => e.stopPropagation()}>
        <MyTitle fontSize='20px' lineHeight='27px' letterSpacing='0.02em'>
          Add new task
        </MyTitle>
      </div>
    </div>
  )
}

export default TaskModal
