const TasksContainer = ({
  MyTitle,
  TransitionGroup,
  CSSTransition,
  Task,
  containerType,
  ...props
}) => {
  const classNames = {
    category: containerType === 'Active' ? 'activeTasks__block' : 'completedTasks__block',
  }

  return (
    <div className={classNames.category}>
      <MyTitle {...props.categoryTitleStyles}>{props.containerType} tasks</MyTitle>

      <TransitionGroup
        className={
          props.darkMode ? 'activeTasks__container darkMode' : 'activeTasks__container'
        }>
        {props.tasksData
          .filter(task => !task.completed)
          .map(uncompletedTask => {
            const nodeRef = props.createRef(null)
            return (
              <CSSTransition
                key={uncompletedTask.time}
                nodeRef={nodeRef}
                timeout={500}
                classNames='task'>
                <Task
                  time={uncompletedTask.time}
                  currentTask={uncompletedTask}
                  tabTitle={props.tabTitle}
                  completed={uncompletedTask.completed}
                  deleteTask={props.tasksMethods.deleteTask}
                  checkTask={props.tasksMethods.checkTask}
                  editTask={props.tasksMethods.editTask}
                  timeLine={props.timeLine}
                  ref={nodeRef}>
                  {uncompletedTask.task}
                </Task>
              </CSSTransition>
            )
          })}
      </TransitionGroup>
    </div>
  )
}

export default TasksContainer
