import React, { useState } from 'react'
import cl from './addBtn.module.scss'
import Input from '../Input/Input'

const AddBtn = () => {
  const [state, setState] = useState({ invisible: false, visible: false })

  const btnClasses = [cl.addBtn__btn]

  if (invisible) {
    btnClasses.push(cl.invisible)
  }

  return (
    <div className={cl.addBtn__container}>
      <div className={cl.addBtn__input}>
        <Input padding='6px' fontSize='14px' />
      </div>
      <button
        type='button'
        className={btnClasses.join(' ')}
        onClick={() => setState({ invisible: true, visible: true })}>
        add category
      </button>
    </div>
  )
}

export default AddBtn
