import React, { useState } from 'react'
import './App.scss'
import AsideBar from '../AsideBar/AsideBar'
import MainContent from '../MainContent/MainContent'
import TaskModal from '../TaskModal/TaskModal'

const defaultItems = [
  { title: 'Home', data: [] },
  { title: 'Work', data: [] },
  { title: 'Sport', data: [] },
]

const App = () => {
  const [darkMode, setDarkMode] = useState(true)
  const [tabItems, setTabItem] = useState(defaultItems)
  const [tab, setTab] = useState(defaultItems[0].title)
  const [taskModal, setTaskModal] = useState(false)

  const createTask = (task, setTask) => {
    setTask({ input: '', select: '' })
    setTaskModal(false)

    setTasksList({ task: task.input, date: new Date(), completed: false })

    tabItems.forEach(tab => {
      if (tab.title === task.select) {
        tab.data.push({ task: task.input, date: new Date(), completed: false })
      }
    })

    return setTabItem(tabItems)
  }

  const deleteTask = (event, title, currentTask) => {
    event.stopPropagation()

    tabItems.forEach(tab => {
      if (title === tab.title) {
        tab.data = tab.data.filter(task => task.task !== currentTask.task)
      }
    })

    return setTabItem(tabItems)
  }

  return (
    <div className={darkMode ? 'App darkMode' : 'App'}>
      <div className='app__container'>
        <AsideBar
          darkMode={darkMode}
          tabItems={tabItems}
          setTabItem={setTabItem}
          tab={tab}
          setTab={setTab}
        />
        <MainContent
          darkMode={darkMode}
          setDarkMode={setDarkMode}
          setTaskModal={setTaskModal}
          tabItems={tabItems}
          setTabItem={setTabItem}
          deleteTask={deleteTask}
          tab={tab}
          setTab={setTab}
        />
        <TaskModal
          darkMode={darkMode}
          taskModal={taskModal}
          setTaskModal={setTaskModal}
          tabItems={tabItems}
          createTask={createTask}
        />
      </div>
    </div>
  )
}

export default App
