import React, { forwardRef } from 'react'
import Input from '../../../../Components/UI/Input/Input'
import MainBtn from '../../../../Components/UI/MainBtn/MainBtn'
import useValue from '../../../../hooks/useValue'

const Registration = forwardRef(({ ...props }, ref) => {
  const [registration, setRegistration] = useValue({ Email: '', Password: '' })
  const [showPass, setShowPass] = useValue(false)

  const app = initializeApp(firebaseConfig)
  const auth = getAuth(app)

  const registrationEmailPassword = async e => {
    e.preventDefault()

    if (registration.Password) {
      try {
        const userCredential = await createUserWithEmailAndPassword(
          auth,
          registration.Email,
          registration.Password,
        )

        setRegistration({ Email: '', Password: '' })

        setTimeout(() => {
          props.setAuthModal(false)
          props.setAuthorization(true)
        }, 1000)

        props.createAuthInfoModal({
          modal: true,
          type: 'Success',
          text: 'Registration successfully',
        })
      } catch (Error) {
        props.createAuthInfoModal({ modal: true, type: 'Error', text: Error.code })
      }
    }
  }

  return (
    <form className='auth__form'>
      <Input
        style={props.inputStyles}
        placeholder='E-mail'
        type='email'
        value={registration.Email}
        onChange={e => setRegistration({ ...registration, Email: e.target.value })}
        required
        ref={ref}
      />
      <div className='inputPass__container'>
        <Input
          style={props.inputStyles}
          placeholder='Create password'
          type={showPass ? 'text' : 'password'}
          value={registration.Password}
          onChange={e => setRegistration({ ...registration, Password: e.target.value })}
          minLength={6}
          required
        />
        <button
          type='button'
          className='inputPass__showBtn'
          onClick={() => setShowPass(!showPass)}>
          {showPass ? <span>&#128269;</span> : <span>&#128270;</span>}
        </button>
      </div>

      <MainBtn type='submit' styles={props.btnStyles} onClick={registrationEmailPassword}>
        Create account
      </MainBtn>
    </form>
  )
})

export default Registration
