import React, { useContext } from 'react'
import { Context } from '../../../../context'
import useValue from '../../../../hooks/useValue'
import MyModal from '../../../../Components/UI/MyModal/MyModal'
import MyTitle from '../../../../Components/MyTitle/MyTitle'
import Select from '../../../../Components/UI/Select/Select'

const Statistics = () => {
  const { darkMode, modals, openModals } = useContext(Context)

  const styleObj = {
    modalStyles: {
      modal: {
        backgroundColor: '#0007',
        transition: 'all .0.2s ease 0s',
      },
      modalContainer: {
        width: '700px',
        padding: '20px',
        borderRadius: '10px',
        transition: 'all 0.5s ease 0.1s',
      },
    },
    titleStyles: {
      fontWeight: '700',
      fontSize: '20px',
      lineHeight: '27px',
      letterSpacing: '0.02em',
    },
    selectStyles: {
      width: '160px',
      padding: '8px 15px',
      border: '1px solid rgba(40, 40, 70, 0.3)',
      borderRadius: '8px',
      fontWeight: '400',
      fontSize: '16px',
      lineHeight: '19px',
      letterSpacing: '0.02em',
      option: {
        fontSize: '14px',
        color: '#666',
      },
    },
  }

  if (window.innerWidth <= 767) {
    styleObj.modalStyles.modalContainer.width = '330px'
    styleObj.modalStyles.modalContainer.padding = '15px'
    styleObj.modalStyles.modalContainer.borderRadius = '8px'
  }

  if (window.innerWidth <= 414) {
    styleObj.modalStyles.modalContainer.width = '290px'
    styleObj.modalStyles.modalContainer.padding = '10px'
  }

  return (
    <MyModal
      styles={styleObj.modalStyles}
      darkMode={darkMode}
      opened={modals.statisticsModal}
      closeModal={() => openModals({ ...modals, statisticsModal: false })}>
      <MyTitle {...styleObj.titleStyles}>Statistics</MyTitle>
      <Select
        styles={styleObj.selectStyles}
        options={[{ title: 'Day' }, { title: 'Month' }, { title: 'Year' }]}
      />
    </MyModal>
  )
}

export default Statistics
