import React from 'react'
import cl from './myModal.module.scss'

const MyModal = ({ opened, darkMode, children }) => {
  const classes = { modal: [cl.modal], modal__container: [cl.modal__container] }

  opened && classes.modal.push(cl.open)
  darkMode && classes.modal__container.push(cl.darkMode)

  return (
    <div className={classes.modal.join(' ')} onClick={props.closeModal}>
      <div
        className={classes.modal__container.join(' ')}
        onClick={e => e.stopPropagation()}>
        {children}
      </div>
    </div>
  )
}

export default MyModal
