import React, { useEffect, useState } from 'react'
import './time.scss'
import MyTitle from '../../MyTitle/MyTitle'

const Time = ({ darkMode }) => {
  const [time, setTime] = useState(new Date())

  useEffect(() => {
    setInterval(() => {
      setTime(new Date())
    }, 100)
  }, [])

  return (
    <div className={darkMode ? 'mainContent__time darkMode' : 'mainContent__time'}>
      <MyTitle fontSize='18px' lineHeight='25px' letterSpacing='0.02em'>
        Wel wel wel!
      </MyTitle>
      <div className='time__container'>
        <div className='time__block left'>
          <span className='time__helpTitle'>Now on the clock</span>
          <div className='time__block-clock'>
            <span>{time.toLocaleTimeString()}</span>{' '}
          </div>
        </div>
        <div className='time__block right'>
          <span className='time__helpTitle'>Today we have</span>
          <div className='time__block-clock'>
            <span>{time.toLocaleDateString()}</span>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Time
