const getDataFromLS = key => JSON.parse(localStorage.getItem(key))

const addDataInLS = (key, value) => {
  const getCircularReplacer = () => {
    const seen = new WeakSet()
    return (Rkey, Rvalue) => {
      if (typeof Rvalue === 'object' && Rvalue !== null) {
        if (seen.has(Rvalue)) {
          return
        }
        seen.add(Rvalue)
      }
      return Rvalue
    }
  }

  return localStorage.setItem(key, JSON.stringify(value, getCircularReplacer()))
}

export { getDataFromLS, addDataInLS }
